package cqupt.edu.zmh.previous;

import org.junit.Test;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

/***
 * https://leetcode.cn/problems/find-all-anagrams-in-a-string/
 */
public class FindAllAnagramsInAString {
    public List<Integer> findAnagrams3(String s, String p) {
        List<Integer> list = new ArrayList<>();
        if (p.length() > s.length()) return list;
        byte[] bytes = p.getBytes();
        Arrays.sort(bytes);
        String p1 = Arrays.toString(bytes);
        int len = s.length() - p.length() + 1;
        for (int i = 0; i < len; i++) {
            String subStr = s.substring(i, i + p.length());
            byte[] subStrBytes = subStr.getBytes();
            Arrays.sort(subStrBytes);
            String s1 = Arrays.toString(subStrBytes);
            if (s1.equals(p1)){
                list.add(i);
            }
        }
        return list;
    }

    // 思路：用两个大小都为26的int数组分别保存s的字串和p，然后一步步滑动s，减去s的头部增加s的尾部相当于就移动了s
    public List<Integer> findAnagrams2(String s, String p) {
        int sLen = s.length();
        int pLen = p.length();
        List<Integer> list = new ArrayList<>();
        if (pLen > sLen) return list;
        int[] sCount = new int[26];
        int[] pCount = new int[26];
        for (int i = 0; i < pLen; i++) {
            sCount[s.charAt(i) - 'a']++;
            pCount[p.charAt(i) - 'a']++;
        }
        if (Arrays.equals(sCount,pCount)){
            list.add(0);
        }
        for (int i = 0; i < sLen - pLen; i++) {
            // 更新s:减去s的头部，加上s的尾部
            sCount[s.charAt(i) - 'a']--;
            sCount[s.charAt(i+pLen) - 'a']++;
            if (Arrays.equals(sCount,pCount)){
                list.add(i + 1);
            }
        }
        return list;
    }

    public List<Integer> findAnagrams(String s, String p) {
        int sLen = s.length(), pLen = p.length();

        if (sLen < pLen) {
            return new ArrayList<Integer>();
        }

        List<Integer> ans = new ArrayList<Integer>();
        int[] count = new int[26];
        for (int i = 0; i < pLen; ++i) {
            ++count[s.charAt(i) - 'a'];
            --count[p.charAt(i) - 'a'];
        }

        int differ = 0;
        for (int j = 0; j < 26; ++j) {
            if (count[j] != 0) {
                ++differ;
            }
        }

        if (differ == 0) {
            ans.add(0);
        }

        for (int i = 0; i < sLen - pLen; ++i) {
            if (count[s.charAt(i) - 'a'] == 1) {  // 窗口中字母 s[i] 的数量与字符串 p 中的数量从不同变得相同
                --differ;
            } else if (count[s.charAt(i) - 'a'] == 0) {  // 窗口中字母 s[i] 的数量与字符串 p 中的数量从相同变得不同
                ++differ;
            }
            --count[s.charAt(i) - 'a'];

            if (count[s.charAt(i + pLen) - 'a'] == -1) {  // 窗口中字母 s[i+pLen] 的数量与字符串 p 中的数量从不同变得相同
                --differ;
            } else if (count[s.charAt(i + pLen) - 'a'] == 0) {  // 窗口中字母 s[i+pLen] 的数量与字符串 p 中的数量从相同变得不同
                ++differ;
            }
            ++count[s.charAt(i + pLen) - 'a'];

            if (differ == 0) {
                ans.add(i + 1);
            }
        }

        return ans;
    }

    @Test
    public void test(){
        long start = System.currentTimeMillis();
//        String s = "";
//        String p = "";
        String s = "cbaebabacd";
        String p = "abc";
        List<Integer> list = findAnagrams(s,p);
        long end = System.currentTimeMillis();
        System.out.println("花费时间:"+ (end - start));
        for (Integer integer : list) {
            System.out.println(integer);
        }
    }
}
